export default function Card() {
    
    let ranNum = Math.floor(Math.random() * 20 + 1);
    // let myCat = "dog"
    
    let request = new XMLHttpRequest();
    request.open("GET", "https://pixabay.com/api/?key=35904460-6da0f483724d8177c3f681e67&q=photo")
    request.send();
    request.onload = () => {
        let resObj = JSON.parse(request.response)
        let ranImg = resObj.hits[ranNum]
        console.log(ranImg);
    }
    
  return (
    <div>
        <image ref={ranImg} ></image>
    </div>
  )
}
