import React, {useState} from 'react'

export default function Card() {
    
    const [ranImg, setRanImg] = useState()
    
    let ranNum = Math.floor(Math.random() * 20 + 1);
    // let ranImg = ""
    
    let request = new XMLHttpRequest();
    request.open("GET", "https://pixabay.com/api/?key=35904460-6da0f483724d8177c3f681e67&q=photo")
    request.send();
    request.onload = () => {
        let resObj = JSON.parse(request.response)
        setRanImg(resObj.hits[ranNum].largeImageURL)
        // ranImg = resObj.hits[ranNum]
        console.log(resObj.hits[ranNum].largeImageURL);
    }
    
  return (
    <div>
        <img src={ranImg} />
    </div>
  )
}
