import React, {useEffect, useState} from 'react'
import $ from 'jquery';

export default function Card({ picBank }) {
    const [ranImg, setRanImg] = useState("")
    let tempNum = Math.floor(Math.random() * 20)

  async function fetchCard() {
    fetch("https://pixabay.com/api/?key=35904460-6da0f483724d8177c3f681e67&q=photo")
        .then((response) => response.json())
        .then((data) => {picBank.push(data.hits[tempNum])
          setRanImg(data.hits[tempNum].largeImageURL)
        })
        console.log(picBank)
  }
    // setRanImg(data.hits[tempNum].largeImageURL)
    useEffect(() => {
        fetchCard()
    }, [])

    function handleClick(event){
      // console.log(event)
      event.target.style.transform = "rotateY(180deg)";
    }

    // $(".card-inner").css("transform", "rotateY(180deg)")
  return (<>
    <div className='card' onClick={(e)=>handleClick(e)}>
      <div className="card-inner">
        <div className="card-front">
        <img className="img" alt="card" src={ranImg} />
        </div>
        <div className="card-back">
          this is the back
        </div>
      </div>
    </div>

    <div className='card' onClick={(e)=>handleClick(e)}>
      <div className="card-inner">
        <div className="card-front">
        <img className="img" alt="card" src={ranImg} />
        </div>
        <div className="card-back">
          this is the back
        </div>
      </div>
    </div>
    </>)
}
