import React, {useState, useEffect} from 'react'
import Card from './Card'

//      --- TODOS ---     //

// create final game state
// timer?
// input to pull new images

export default function Game() {
  const [active, setActive] = useState([])
  const data = {
    active: active,
    setActive: setActive,
  }
  useEffect(()=>{
    Array.from(document.querySelectorAll(".card-back")).forEach((el)=> {
      el.addEventListener("click", ((event) => {
        event.target.parentElement.style.transform = "rotateY(0deg)"
        setActive(prev => [...prev, event.target])
      }))
    })

  }, [])
  //    --- Compare cards currently in Active array
  useEffect(() =>{
    if(active.length === 2){
      if(active[0].className === active[1].className){
        setActive([])
      } else {
        setTimeout(()=>{
          active[0].parentElement.style.transform = "rotateY(180deg)"
          active[1].parentElement.style.transform = "rotateY(180deg)"
          setActive([])
        }, 1000)
      }
    }

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [active.length])

  return (
    <div id='game'>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
    </div>
  )
}
