import React from 'react'

export default function Card() {
    
    let ranNum = Math.floor(Math.random() * 20 + 1);
    
    let request = new XMLHttpRequest();
    request.open("GET", "https://pixabay.com/api/?key=35904460-6da0f483724d8177c3f681e67&q=yellow+flowers&image_type=photo")
    request.send();
    request.onload = () => {
        let resObj = JSON.parse(request.response)
        let ranImg = resObj.hits[ranNum]
        // console.log(resObj.hits[ranNum]);
    }
    
  return (
    <div>
        <image ref={ranImg} ></image>
    </div>
  )
}
