import React, {useState, useEffect} from 'react'
import Card from './Card'

export default function Game() {
  
  // random number for card
  let tempNum = Math.floor(Math.random() * 20 + 1)
  const [ranNum, setRanNum] = useState([tempNum])
  
  
  let array = [5, 6, 2, 10]
  
  if(array.includes(5)) { console.log("yep")}
  
  function setRandomNum () {
    setRanNum( prevNum => {
      if(prevNum.includes(tempNum)){
        setRanNum(tempNum)
      }
      console.log(prevNum)
    })
    
  }
  
  useEffect(() => {
    setRandomNum()
  }, [])
  
  
  
  
  
  return (
    <div>
        <Card ranNum={ranNum} setRanNum={setRanNum}/>
        <Card ranNum={ranNum} setRanNum={setRanNum}/>
        <Card ranNum={ranNum} setRanNum={setRanNum}/>
        <Card ranNum={ranNum} setRanNum={setRanNum}/>
        <Card ranNum={ranNum} setRanNum={setRanNum}/>
        <Card ranNum={ranNum} setRanNum={setRanNum}/>
    </div>
  )
}
