import React, {useState, useEffect} from 'react'
import Card from './Card'

export default function Game() {
  const [active, setActive] = useState([])
  const data = {
    picBank: [],
    active: active,
    setActive: setActive,
  }

  useEffect(() =>{
    if(active.length === 2){
      if(active[0].className === active[1].className){
        setActive([])
      } else {
        setTimeout(()=>{
          active[0].parentElement.style.transform = "rotateY(180deg)"
          active[1].parentElement.style.transform = "rotateY(180deg)"
          setActive([])
        }, 1000)
      }
    }
    console.log(active)
  }, [active.length])

  return (
    <div id='game'>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
        <Card {...data}/>
    </div>
  )
}
