import $ from 'jquery';

export default function Card({ranNum}) {

    const [ranImg, setRanImg] = useState("")
    let tempNum = Math.floor(Math.random() * 20 + 1)
    // console.log(ranNum())

    function fetchCard() {
        fetch("https://pixabay.com/api/?key=35904460-6da0f483724d8177c3f681e67&q=photo")
            .then((response) => response.json())
            .then((data) => setRanImg(data.hits[tempNum].largeImageURL))
    }
    useEffect(() => {
        fetchCard()
    }, [])

